import { ADD_TO_CART } from '../actions/actionTypes'
const cartReducer = (state,action) => { 
    if (state === undefined) {
        state = {
            cartItems: [],
            numberOfItems: 0,
            totalCost: parseFloat(0.00)
        }
    }
    switch (action.type) {
        case ADD_TO_CART:
            return {
                ...state,

            }
    }
}

export default cartReducer
