import { ADD_TO_CART } from './types'

export const addToCart = (item) => {

    return {
        type: ADD_TO_CART,
        product: item
    }
}
export const deleteItemFromCard = (itemID) => {

    return {
        type: DELETE_ITEM_FROM_CARD,
        product: item
    }
}
