import { types } from '../actions/actionTypes'

const productsReducer = (state, action) => { 
    if (state === undefined) {
        state = {
            products:[]
        }
        switch (action.type) {
            case LOAD_PRODUCTS:
                state = {
                    ...state,
                    products: action.data

                }

            default:
                return state
        }

    }
}

export default productsReducer
